CREATE TABLE IF NOT EXISTS users (id INT NOT NULL AUTO_INCREMENT PRIMARY KEY, firstname VARCHAR(255), lastname VARCHAR(255), email VARCHAR (255), password VARCHAR(255), created_at DATETIME DEFAULT CURRENT_TIMESTAMP) DEFAULT CHARACTER SET utf8 ENGINE=InnoDB;

CREATE TABLE IF NOT EXISTS posts (id INT NOT NULL AUTO_INCREMENT PRIMARY KEY, user_id INT, title VARCHAR(255), body TEXT, created_at DATETIME DEFAULT CURRENT_TIMESTAMP) DEFAULT CHARACTER SET utf8 ENGINE=InnoDB;